import React from 'react';
import WeatherCard from './WeatherCard';
import moment from 'moment';

class WeatherList extends React.Component {

	state = { data: this.props.weather };

	componentDidMount() {
		const weather = this.state.data.map(day => {
			return <WeatherCard 
						date={moment(day.dt*1000).format('ddd, MMM Do')}
						type={day.weather[0].description}
						temp={Math.round((day.main.temp)*9/5 - 459.67)}
						icon={day.weather[0].icon}
						key = {day.dt}
					/>
		})
	}

	componentDidUpdate(previousProps, previousState) {
		if (previousProps.weather !== this.props.weather) {
