import React from 'react';
import SearchBar from './SearchBar';
import WeatherCard from './WeatherCard';
import './App.css';

class App extends React.Component {

	state = { type: '', date: '' };

	onSearchSubmit = async term => {
		let weatherData = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${term}&appid=8c5fb53f5e47ec25460b34d5e83664e9`);
		weatherData = await weatherData.json();
		console.log(weatherData);
		this.setState({ 
			type: weatherData.list[0].weather[0].main,
			date: weatherData.list[0].dt_txt
			 });
	}



	render() {
		return (
			<div className='ui container'>
				<div className='search'>
					<SearchBar onSubmit={this.onSearchSubmit} />
				</div>
				<div className='forecast-box'>
					<WeatherCard type={this.state.type} date={this.state.date}/>
					<WeatherCard type={this.state.type} date={this.state.date}/>
					<WeatherCard type={this.state.type} date={this.state.date}/>
					<WeatherCard type={this.state.type} date={this.state.date}/>
					<WeatherCard type={this.state.type} date={this.state.date}/>
				</div>
			</div>
		)
	}
}

export default App;

