import React from 'react';
import SearchBar from './SearchBar';
import WeatherCard from './WeatherCard';
import './App.css';
import moment from 'moment';

class App extends React.Component {

	state = { type: [], date: []}; //make date an array and have each of the 5 dates elements of the array

	onSearchSubmit = async term => {
		let weatherData = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${term}&appid=8c5fb53f5e47ec25460b34d5e83664e9`);
		weatherData = await weatherData.json();
		const { list } = weatherData;
		const dailyWeather = list.filter((item) => {
			return item.dt_txt.includes('15:00:00');
		})
		console.log(dailyWeather);
		const date = dailyWeather.map(day => moment(day.dt*1000).format('MMM'));
		console.log(date);
		const type = dailyWeather.map((day) => day.weather[0].description);
		this.setState({ 
			type,
			date
			 });
	}



	render() {
		return (
			<div className='ui container'>
				<div className='search'>
					<SearchBar onSubmit={this.onSearchSubmit} />
				</div>
				<div className='forecast-box'>
					<WeatherCard date={this.state.date[0]} type={this.state.type[0]}/>
					<WeatherCard date={this.state.date[1]} type={this.state.type[1]}/>
					<WeatherCard date={this.state.date[2]} type={this.state.type[2]}/>
					<WeatherCard date={this.state.date[3]} type={this.state.type[3]}/>
					<WeatherCard date={this.state.date[4]} type={this.state.type[4]}/>
				</div>
			</div>
		)
	}
}

export default App;

