groupByDate = responseData => {
	const today = new Date();
	const day = 60 * 60 * 24 * 1000;

	const dateBins = {};
	const nBins = 6; // there can be reports for up to 6 distinct dates

	for (let i = 0; i < nBins; i++) {
	    // set up a bin (empty array) for each date
		const date = new Date(today.getTime() + i * day);
		dateBins[date.getDate()] = [];
	}
	const reports = responseData;
	for (const report of reports) {
  	const reportDate = Number(report.dt_txt.slice(8,10));
  	dateBins[reportDate].push(report);
  }
  temps(dateBins);
}

temps = dailyReport => {
  let dailyData = { maxTemp: [], minTemp: [] };
  let max = 0;
  let min = 0;
  for (let i = 0; i < 6; i++) {
  	max = dailyReport[Object.keys(dailyReport)[i]].reduce((max, current) => current.main.temp_max > max ? current.main.temp_max : max, 0);
  	min = dailyReport[Object.keys(dailyReport)[i]].reduce((min, current) => current.main.temp_min < min ? current.main.temp_min : min, 1000);
  	dailyData['maxTemp'].push(max);
  	dailyData['minTemp'].push(min);
	}
	return dailyData;
}

