import { combineReducers } from 'redux';

const weatherData = (state=[], action) => {
	switch(action.type) {
		case 'WEATHER_DATA':
			const daily = action.payload.list.filter((item) => {
				item.dt_txt.includes('15:00:00');
			})
			return daily.list;
		default:
			return state;
	}
}

const cityData = (state={}, action) => {
	switch(action.type) {
		case 'CITY_DATA':
			return action.payload;
		default:
			return state;
	}
}

const searchTerm = (state='', action) => {
	switch(action.type) {
		case 'SEARCH_TERM':
			return action.payload
		default:
			return state;
	}
}

export default combineReducers({ term: searchTerm, weather: weatherData, city: cityData });
