import React from 'react';
import WeatherCard from './WeatherCard';
import moment from 'moment';

const filter = weatherData => {
	let filteredWeather = [];
	for (let i = 0; i < weatherData.length; i+=8) {
		filteredWeather.push(weatherData[i]);
	}
	console.log(filteredWeather);
	return filteredWeather;
}

const WeatherList = props => {
	const dailyWeather = filter(props.weather);
	const weather = dailyWeather.map(day => {
		return <WeatherCard 
						date={moment(day.dt*1000).format('ddd, MMM Do')}
						type={day.weather[0].description}
						icon={day.weather[0].icon}
						temp={day.main.temp}
						key={day.dt}
					/>
	});

	return (
		<div className='forecast-box'>
			{weather}
		</div>
	)
}


export default WeatherList;
