import { combineReducers } from 'redux';

// const weatherData = (state=[], action) => {

// }

const searchTerm = (state='', action) => {
	switch(action.type) {
		case 'SEARCH_TERM':
			return action.payload
		default:
			return state;
	}
}

export default combineReducers({ term: searchTerm });
