import React from 'react';
import SearchBar from './SearchBar';
import WeatherCard from './WeatherCard';
import './App.css';
import moment from 'moment';

class App extends React.Component {

	state = { type: [], date: [], temp: []}; 

	onSearchSubmit = async term => {
		let weatherData = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${term}&appid=8c5fb53f5e47ec25460b34d5e83664e9`);
		weatherData = await weatherData.json();
		const { list } = weatherData;
		const dailyWeather = list.filter((item) => {
			return item.dt_txt.includes('15:00:00');
		})
		console.log(dailyWeather);
		const date = dailyWeather.map(day => moment(day.dt*1000).format('MMM Do'));
		const type = dailyWeather.map(day => day.weather[0].description);
		const temp = dailyWeather.map(day => (day.weather.main.temp)*9/5 - 459.67);
		this.setState({ 
			type,
			date,
			temp
			 });
	}



	render() {
		return (
			<div className='ui container'>
				<div className='search'>
					<SearchBar onSubmit={this.onSearchSubmit} />
				</div>
				<div className='forecast-box'>
					<WeatherCard date={this.state.date[0]} type={this.state.type[0]} temp={this.state.temp[0]}/>
					<WeatherCard date={this.state.date[1]} type={this.state.type[1]} temp={this.state.temp[1]}/>
					<WeatherCard date={this.state.date[2]} type={this.state.type[2]} temp={this.state.temp[2]}/>
					<WeatherCard date={this.state.date[3]} type={this.state.type[3]} temp={this.state.temp[3]}/>
					<WeatherCard date={this.state.date[4]} type={this.state.type[4]} temp={this.state.temp[4]}/>
				</div>
			</div>
		)
	}
}

export default App;
