import React from 'react';
import SearchBar from './SearchBar';
import WeatherList from './WeatherList';
import './App.css';

class App extends React.Component {

	state = { weather: [], temp: [], city: {} }; 

	onSearchSubmit = async term => {
		let weatherData = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${term}&appid=8c5fb53f5e47ec25460b34d5e83664e9`);
		weatherData = await weatherData.json();
		const { list, city } = weatherData;
		const dailyWeather = list.filter((item) => {
			return item.dt_txt.includes('15:00:00');
		})
		this.setState({ weather: dailyWeather, city: city });
	}


	render() {
		return (
			<div className="ui container">
				<div className="search">
					<SearchBar onSubmit={this.onSearchSubmit} />
				</div>
				<div className="city">
					{this.state.city.name && this.state.city.country ? `${this.state.city.name}, ${this.state.city.country}`: ''}
				</div>
        <WeatherList weather={this.state.weather} />
			</div>
		)
	}
}


export default App;
