import { combineReducers } from 'redux';

const initialState = {
	weather: [],
	city: {},
	term: ''
}

const responseData = (initialState, action) => {
	switch(action.type) {
		case 'WEATHER_DATA':
			const daily = action.payload.list.filter((item) => {
				return item.dt_txt.includes('15:00:00');
			})
			return Object.assign({}, initialState, {weather: daily});
		case 'CITY_DATA':
			return Object.assign({}, initialState, { city: action.payload});
		default:
			return initialState;
	}
}

const searchTerm = (initialState, action) => {
	switch(action.type) {
		case 'SEARCH_TERM':
			return Object.assign({}, initialState, {term: action.payload});
		default:
			return initialState;
	}
}

export default combineReducers({ searchTerm, responseData });
