import React from 'react';
import SearchBar from './SearchBar';
import WeatherCard from './WeatherCard';
import './App.css';

class App extends React.Component {

	state = { type: [], date: ''}; //make date an array and have each of the 5 dates elements of the array

	onSearchSubmit = async term => {
		let weatherData = await fetch(`https://api.openweathermap.org/data/2.5/forecast?q=${term}&appid=8c5fb53f5e47ec25460b34d5e83664e9`);
		weatherData = await weatherData.json();
		console.log(weatherData);
		const dates = [];
		const type = [];
		//Looping through weather data to create an array with the next 5 days' dates
		for (let i = 0; i < 33; i+=8) {
			dates.push(weatherData.list[i].dt_txt);
		}
		//Looping through weather data to create an array with the next 5 days' dates
		for (let i = 0; i < 33; i+=8) {
			type.push(weatherData.list[i].weather[0].main);
		}
		this.setState({ 
			type: type,
			date: dates
			 });
	}



	render() {
		return (
			<div className='ui container'>
				<div className='search'>
					<SearchBar onSubmit={this.onSearchSubmit} />
				</div>
				<div className='forecast-box'>
					<WeatherCard type={this.state.type[0]} date={this.state.date[0]}/>
					<WeatherCard type={this.state.type[1]} date={this.state.date[1]}/>
					<WeatherCard type={this.state.type[2]} date={this.state.date[2]}/>
					<WeatherCard type={this.state.type[3]} date={this.state.date[3]}/>
					<WeatherCard type={this.state.type[4]} date={this.state.date[4]}/>
				</div>
			</div>
		)
	}
}

export default App;

