import React from 'react';
import WeatherCard from './WeatherCard';
import moment from 'moment';

const WeatherList = props => {

	groupByDate = () => {
		const today = new Date();
		const day = 60 * 60 * 24 * 1000;

		const dateBins = {};
		const nBins = 6; // there can be reports for up to 6 distinct dates

		for (let i = 0; i < nBins; i++) {
		    // set up a bin (empty array) for each date
			const date = new Date(today.getTime() + i * day);
			dateBins[date.getDate()] = [];
		}
		const reports = props.weather;
		for (const report of reports) {
    	const reportDate = new Date(report.dt * 1000).getDate();
    	dateBins[reportDate].push(report);
    }
    console.log(dateBins);
}


	const weather = props.weather.map(day => {
		return <WeatherCard 
							date={moment(day.dt*1000).format('ddd, MMM Do')}
							type={day.weather[0].description}
							temp={Math.round((day.main.temp)*9/5 - 459.67)}
							icon={day.weather[0].icon}
							key = {day.dt}
						/>
	})

	return (
		<div className='forecast-box'>
			{weather}
		</div>
	)

}


export default WeatherList;
